doc.extras.metadata.source.portal_link='https://www.govdata.de/';
let contributor = DcatMapper.select('./dcatde:contributorID/@rdf:resource', this.record, true);
if(contributor && contributor.textContent.endsWith("mCloud")){
	this.skipped = true;
}
if(!doc.distribution || doc.distribution.length === 0){
	this.skipped = true;
}
switch(doc.extras.display_contact[0].name){
    case 'GovData DCAT-AP':
        if(doc.title.indexOf('Binnenschiffahrt') !== -1 || doc.title.indexOf('WRRL') !== -1) doc.extras.subgroups = ['waters'];
        else if(doc.title.indexOf('Fahrplandaten') !== -1) doc.extras.subgroups = ['roads', 'railway'];
        else if(doc.title.indexOf('Luftverkehr') !== -1) doc.extras.subgroups = ['aviation'];
        else  this.skipped = true;
        //Schlagwort 'gewässernetz' oder 'hydrographie' vorhanden --> 'Wasserstraßen'
        //Schlagwort 'umweltüberwachung': Standard-Kategorie 'Wasserstraßen',

        //außer bei 'Niederschlagsmessnetz', 'Agrarmeteorologisches Messnetz', 'Luft- und Depositionsmessnetz Sachsen (AG Mz)' --> 'Wetter & Klima'
        //außer bei 'Straßenzustands- und Wetter-Informationssystem (SWIS)', 'Immissionskataster' --> 'Wetter & Klima' & 'Straßen'
        //außer bei 'Kartierungsgebiet', 'Belastete je Gemeinde' --> 'Straßen', 'Bahn', 'Luft- und Raumfahrt'

        //UUIDs ausschließen: b11bc8e2-034b-40ea-b1d6-eb3c1ec441ff, f100f81b-22a8-56b4-8f5a-340d15fd8106, eb523000-c2f9-43b6-bac1-267dd015b775, ac2293e2-62a6-4889-b9c4-3534cbdc27bd, 4b4b6499-a473-44f3-baa8-864d426fce0d
        break;
    case 'GovData: Statistisches Bundesamt':
        if(doc.title.indexOf('Berichtsflughafen') !== -1 || doc.title.indexOf('Luftverkehr') !== -1) doc.extras.subgroups = ['aviation'];
        else if(doc.title.indexOf('Eisenbahngüterverkehr') !== -1 || doc.title.indexOf('Schienennetzes') !== -1) doc.extras.subgroups = ['railway'];
        else if(['Seeverkehr', 'Seegüterumschlag', 'Binnensch.', 'Binnenschifffahrt', 'Fährverkehr', 'Seehäfen'].some(s => doc.title.indexOf(s) !== -1))  doc.extras.subgroups = ['waters'];
        else if(doc.title.indexOf('Rohrfernleitungsstatistik') !== -1) doc.extras.subgroups = ['infrastructure'];
        else if(doc.title.indexOf('Bussen und Bahnen') !== -1) doc.extras.subgroups = ['roads', 'railway'];
        else doc.extras.subgroups = ['roads'];
        break;
    case 'GovData: Regionalverband Ruhr':
        if(doc.title.indexOf('Verkehr') !== -1 || doc.title.indexOf('Radfahren') !== -1 || doc.title.indexOf('ÖPNV') !== -1)
			doc.extras.subgroups = ['roads'];
        else if (doc.title.indexOf('Gewässer') !== -1)
			doc.extras.subgroups = ['waters'];
        else this.skipped = true;
        break;
    case 'GovData: Nationalparkverwaltung Niedersächsisches Wattenmeer':
        doc.extras.subgroups = ['waters'];
        break;
    case 'GovData: Nationalparkverwaltung Niedersächsisches Wattenmeer / Niedersächsischer Landesbetrieb für Wasserwirtschaft Küsten- und Naturschutz':
        doc.extras.subgroups = ['waters'];
        break;
    case 'GovData: Stadt Trier':
        if(doc.title.indexOf('Oberflächengewässer') !== -1) doc.extras.subgroups = ['waters'];
        else if(['Fahrrad', 'Parken', 'Busparkplätze', 'Nahverkehr', 'Verkehr', 'Behindertenparkplätze', 'Elektromobilität'].some(s => doc.title.indexOf(s) !== -1))  doc.extras.subgroups = ['roads'];
        else this.skipped = true;
        break;
    case 'GovData: SGD Nord - Referat 41 - Landesplanung, Raumordnung':
        if(doc.title.indexOf('Hochgeschwindigkeitsstrecke') !== -1)  doc.extras.subgroups = ['railway'];
        else if(doc.title.indexOf('Straßenverbindung') !== -1 || doc.title.indexOf('Verkehrs') !== -1)  doc.extras.subgroups = ['roads'];
        else if(doc.title.indexOf('Hochwasserschutz') !== -1)  doc.extras.subgroups = ['waters'];
        else this.skipped = true;
        break;
    case 'GovData: Umweltbundesamt':
        if(doc.title.indexOf('Großflughäfen') !== -1 || doc.title.indexOf('Flughäfen') !== -1)  doc.extras.subgroups = ['aviation'];
        else if(doc.title.indexOf('Luftqualitätsdaten') !== -1)  doc.extras.subgroups = ['climate'];
        else if(doc.title.indexOf('Ballungsräume') !== -1)  doc.extras.subgroups = ['roads', 'railway', 'aviation'];
        else if(doc.title.indexOf('Seentypologie') !== -1 || doc.title.indexOf('Einzugsgebiete') !== -1 || doc.title.indexOf('Seen') !== -1) doc.extras.subgroups = ['waters'];
        else if(doc.title.indexOf('Hauptverkehrsstraßen') !== -1)  doc.extras.subgroups = ['roads'];
        else if(doc.title.indexOf('Haupteisenbahnstrecken') !== -1)  doc.extras.subgroups = ['railway'];
        else this.skipped = true;
        break;
    case 'GovData: Thüringer Landesamt für Bodenmanagement und Geoinformation':
        if(doc.title.indexOf('Gewaesser') !== -1 || doc.title.indexOf('Hydro') !== -1) doc.extras.subgroups = ['waters'];
        else if(doc.title.indexOf('Digitale Topographische Karte') !== -1 || doc.title.indexOf('Verkehrsnetze') !== -1 || doc.title.indexOf('Verkehr') !== -1)  doc.extras.subgroups = ['roads', 'railway', 'waters', 'aviation'];
