        break;
    case 'GovData: Niedersächsisches Landesamt für Verbraucherschutz und Lebensmittelsicherheit':
        doc.extras.subgroups = ['waters'];
        break;
    case 'GovData: Landesbetrieb Mobilität Rheinland-Pfalz':
        doc.extras.subgroups = ['roads'];
        break;
    case 'GovData: Gewerbeaufsichtsamt Hildesheim, Zentrale Unterstützungsstelle Luftreinhaltung, Lärm, Gefahrstoffe und Störfallvorsorge (ZUS LLGS)':
        if(doc.title.indexOf('Messstationen') !== -1 || doc.title.indexOf('PM10') !== -1 || doc.title.indexOf('NO2') !== -1)  doc.extras.subgroups = ['roads', 'climate'];
        else if(doc.title.indexOf('Umweltzonen') !== -1)  doc.extras.subgroups = ['roads'];
        else this.skipped = true;
        break;
    case 'GovData: Landkreis Diepholz':
        if(doc.title.indexOf('Strassenmeistereibezirke') !== -1 || doc.title.indexOf('Fahrradleitsystem') !== -1 || doc.title.indexOf('Autobahn') !== -1)  doc.extras.subgroups = ['roads'];
        else this.skipped = true;
        break;
    case 'GovData: Statistische Ämter des Bundes und der Länder':
        doc.extras.subgroups = ['roads'];
        break;
    case 'GovData: Stadt Dresden':
        doc.extras.subgroups = ['waters'];
        if(this.uuid === 'cf1b447c-2d3b-4e85-a61f-fd8ea49f121c') this.skipped = true;
        break;
    case 'GovData: Senatsverwaltung für Umwelt, Verkehr und Klimaschutz Berlin':
        //--> bekommen wir eigentlich über eigene Schnittstelle rein. Hier bitte für mich ein Ticket anlegen, damit ich noch mal prüfe, ob wir Daten ausschließen, die jetzt über den Umweg GovData rein kommen.
        doc.extras.subgroups = ['roads'];
        break;
    case 'GovData: Statistisches Landesamt des Freistaates Sachsen':
        if(doc.title.indexOf('Binnenschifffahrt') !== -1)  doc.extras.subgroups = ['waters'];
        else doc.extras.subgroups = ['roads'];
        break;
    case 'GovData: Bundesamt für Strahlenschutz':
        if(doc.title.indexOf('Bundeswasserstrassen') !== -1 || doc.title.indexOf('Nord- und Ostsee') !== -1)  doc.extras.subgroups = ['waters'];
        else doc.extras.subgroups = ['climate'];
        break;
    case 'GovData: Niedersächsisches Ministerium für Umwelt, Energie, Bauen und Klimaschutz, Referat 14 - Umweltinformation, Digitalisierung, eGovernment':
        doc.extras.subgroups = ['waters'];
        break;
    case 'GovData: Stadt Gelsenkirchen':
