        break;
    case 'GovData: Freie und Hansestadt Bremen':
        if(doc.title.indexOf('Häfen') !== -1)  doc.extras.subgroups = ['waters'];
        else doc.extras.subgroups = ['roads'];
        break;
    case 'GovData: NLWKN':
        doc.extras.subgroups = ['waters'];
        break;
    case 'GovData: Nds. Landesbehörde f. Straßenbau u. Verkehr':
