        break;
    case 'GovData: Senatorin für Klimaschutz, Umwelt, Mobilität, Stadtentwicklung und Wohnungsbau':
        doc.extras.subgroups = ['waters'];
        break;
    case 'GovData: ThüringenForst AöR, Forstliches Forschungs- und Kompetenzzentrum':
        doc.extras.subgroups = ['roads', 'railway'];
        break;
    case 'GovData: NLWKN Betriebsstelle Brake-Oldenburg':
        doc.extras.subgroups = ['waters'];
        break;
    case 'GovData: Verkehrslenkung Berlin':
        doc.extras.subgroups = ['roads'];
        break;
    case 'GovData: Landesamt für Umwelt Brandenburg (LfU), Abteilung Wasserwirtschaft 1 (Genehmigungen / Grundlagen), Referat W16 - Hochwasserrisikomanagement, Wasserrahmenrichtlinie':
        doc.extras.subgroups = ['waters'];
        break;
    case 'GovData: Kataster- und Vermessungsamt':
