    case 'GovData: Barnimer Energiebeteiligungsgesellschaft mbH':
        doc.extras.subgroups = ['roads'];
        break;
    case 'GovData: Landesbetrieb Straßenwesen Brandenburg':
        doc.extras.subgroups = ['roads'];
