        doc.extras.subgroups = ['roads'];
		if(doc.title.indexOf('Gewässer') !== -1 || doc.title.indexOf('Hydro') !== -1)  doc.extras.subgroups = ['waters'];
		if(doc.title.indexOf('Verkehrsnetze') !== -1) doc.extras.subgroups = ['roads', 'railway'];
