query:
PREFIX dcat: <http://www.w3.org/ns/dcat#>
PREFIX dataid: <http://dataid.dbpedia.org/ns/core#>
PREFIX prov: <http://www.w3.org/ns/prov#> 
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX dcatx: <http://www.example.org/>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT DISTINCT ?id ?publisher ?source_link ?title ?description ?distribution_title  ?distribution_url ?issued ?modified ?license (CONCAT("mfund-projekt:LIMBO, mfund-fkz:19F2029, ", GROUP_CONCAT(DISTINCT ?keyword; SEPARATOR=", ")) AS ?keywords) (GROUP_CONCAT(DISTINCT ?dcat_theme; SEPARATOR=", ") AS ?dcat_theme)
WHERE {
?limbo_ds dataid:group ?group;
			dataid:artifact ?artifact;
            dct:identifier ?id;
            dcat:distribution ?limbo_dist;
            dct:issued ?issued;
            dct:license ?limbo_license;
	    rdf:type ?type.

?limbo_dist dcat:downloadURL ?distribution_url ;
            dcatx:localId ?distribution_title.
OPTIONAL {
	?limbo_ds dct:publisher ?publisher.
}

OPTIONAL {
	?limbo_ds void:objectsTarget ?objectsTarget;
			void:subjectsTarget ?subjectsTarget.
	?objectsTarget dataid:artifact ?objectsTarget_artifact.
	?subjectsTarget dataid:artifact ?subjectsTarget_artifact.
}

OPTIONAL {
	?limbo_ds prov:wasDerivedFrom ?mcloud_dist.
	?mcloud_ds dcat:distribution ?mcloud_dist ;
            dct:title ?mcloud_title;
            dct:description ?mcloud_description;
            dcat:keyword ?keyword;
            dcat:theme ?dcat_theme;
            dcat:landingPage ?mcloud_landingPage .
}


BIND("https://data.limbo-project.org/NullLicense,https://datasets.limbo-project.org/NullLicense,http://dcat-ap.de/def/licenses/other-open" as ?null_licenses).
BIND(
       IF(
           CONTAINS(?null_licenses, str(?limbo_license)),
           uri("http://dcat-ap.de/def/licenses/dl-by-de/2.0"),
           ?limbo_license
       )
as ?license).

BIND (
	IF(
		bound(?mcloud_title),
		CONCAT("Limbo-Daten f&uuml;r <a href=\"",?mcloud_landingPage ,"\">",?mcloud_title,"</a> \r\n\r\nOriginal:\r\n",?mcloud_description),
		IF(
			bound(?objectsTarget),
			CONCAT("Sammlung von Links zwischen den Datensammlungen <a href=\"",?subjectsTarget,"\">",?subjectsTarget_artifact,"</a>",
			" und <a href=\"",?objectsTarget,"\">",?objectsTarget_artifact,"</a> im RDF-Format"),
			CONCAT("Datensammlung mit Informationen zu ",?artifact," im RDF-Format")
		)
	)
 as ?description).


BIND (
	IF(
		bound(?mcloud_title),
		CONCAT("LIMBO - ",?mcloud_title),
		CONCAT("LIMBO - ",?artifact)
	)
as ?title).

BIND (?limbo_ds as ?source_link)
BIND (?issued as ?modified)

FILTER (?group = 'org.limbo')

# get the most current version by filtering out any dataset duplicates with earlier release dates
FILTER(!EXISTS {
 ?limbo_ds2 dataid:artifact ?art . 
 ?limbo_ds dataid:artifact ?art . 
 ?limbo_ds2 dct:issued ?issued2 .
 FILTER (?issued2 > ?issued) })

}
GROUP BY ?id ?publisher ?source_link ?title ?description ?distribution_title  ?distribution_url ?issued ?modified ?license
